class Toast extends React.Component {
    state = {
        className: [''],
        msg: 'Oops, Something went wrong. Try again',
        timeoutId: null,
    }

    componentDidUpdate(prevProps) {
        const { toast } = this.props
        if (toast !== prevProps.toast) {
            if (toast.isError) {
                const { error_details } = toast
                if (
                    error_details.err_type &&
                    error_details.err_type === 'REG_CLOSED'
                ) {
                    this.setState({
                        className: ['show', 'error'],
                        msg: `Sorry, registration for the ${error_details.event} is now closed.`,
                    })
                } else if (
                    error_details.err_type &&
                    error_details.err_type.status === 409
                ) {
                    const { data } = error_details.err_type
                    if (data.error) {
                        if (data.error === 'teamname')
                            this.setState({
                                className: ['show', 'warning'],
                                msg: 'The team name is already taken.',
                            })
                        else if (data.error === 'player')
                            this.setState({
                                className: ['show', 'warning'],
                                msg:
                                    'One of the team members has already registered for this event.',
                            })
                    } else
                        this.setState({
                            className: ['show', 'warning'],
                            msg: 'You have already registered for this event',
                        })
                } else {
                    this.setState({
                        className: ['show', 'error'],
                        msg: 'Oops, Something went wrong. Please try again',
                    })
                }
                this.setState({
                    timeoutId: setTimeout(
                        () => this.setState({ className: [''] }),
                        3500
                    ),
                })
            } else {
                const { registration_details } = toast
                this.setState({
                    className: ['show', 'success'],
                    msg: `${registration_details.name}, Registration done successfully. Check your mailbox!`,
                })
                this.setState({
                    timeoutId: setTimeout(
                        () => this.setState({ className: [''] }),
                        3500
                    ),
                })
            }
        }
    }

    onClose = () => {
        this.setState({
            className: [''],
        })
        clearTimeout(this.state.timeoutId)
    }

    render() {
        const { msg, className } = this.state
        return (
            <ToastWrapper className={className.join(' ')}>
                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        columnGap: '1rem',
                        textAlign: 'center',
                    }}
                >
                    {className[1] === 'success' ? (
                        <SuccessIcon />
                    ) : className[1] === 'error' ? (
                        <ErrorIcon />
                    ) : (
                        <WarningIcon />
                    )}
                    {msg}
                    {
                        <CloseButton onClick={this.onClose}>
                            <CloseIcon />
                        </CloseButton>
                    }
                </div>
            </ToastWrapper>
        )
    }
}

const mapStateToProps = (state) => ({
    toast: state.toast,
})

export default connect(mapStateToProps)(Toast)
